import React, {useState, useContext} from 'react';
import {EasyContext} from './Easy.jsx';
import '../App.css';

export default function Letter({letterPos, attemptVal}) {

    const { board, correctWord, currTry } = useContext(EasyContext);
    const letter = board[attemptVal][letterPos];

    var occurance = [...correctWord].reduce((a, e) => { a[e] = a[e] ? a[e] + 1 : 1; return a }, {});
    console.log(occurance); 

    const correct = currTry.noOfAttempt > attemptVal 
        && correctWord[letterPos] === letter;

    const almost = currTry.noOfAttempt > attemptVal 
        && !correct && letter != "" && correctWord.includes(letter);

    const letterState =  currTry.noOfAttempt > attemptVal ?
     (correct ? "correct" : almost ? "almost" : "error") : "normal";

    return (
        <div className = "letter" id = {letterState}>{letter}</div>
    )

}
