import React, {setState, useContext} from 'react';
import {EasyContext} from './Easy.jsx';
import Popover from 'react-bootstrap/Popover';

export default function Key({keyVal, letterLength}) {

    const { board, setBoard, currTry, setCurrTry } = useContext(EasyContext);

    const sentense = "";

    const selectLetter = () => {
        if (keyVal === "ENTER") {
            if (currTry.letterPos !== letterLength) {
                sentense = "Word is too short honey :)";
                return;
            }
            setCurrTry({noOfAttempt: currTry.noOfAttempt + 1, letterPos: 0});
        } else if (keyVal === "DELETE") {
            if (currTry.letterPos === 0) {
                return;
            }
            const newBoard = [...board];
            newBoard[currTry.noOfAttempt][currTry.letterPos - 1] = "";
            setBoard(newBoard);
            setCurrTry({...currTry, letterPos: currTry.letterPos - 1})
        } else {
            if (currTry.letterPos >= letterLength) {
                return;
            }
            const newBoard = [...board];
            newBoard[currTry.noOfAttempt][currTry.letterPos] = keyVal;
            setBoard(newBoard);
            setCurrTry({...currTry, letterPos: currTry.letterPos + 1})
        }
    }

    const popover = (
        <Popover id="popover-basic">
          <Popover.Header as="h3">Opps</Popover.Header>
          <Popover.Body>
            {sentense}
          </Popover.Body>
        </Popover>
      );

    return (
        <div>
            <div onClick = {selectLetter}>
                {keyVal}
            </div>
            <p>{sentense}</p>
        </div>
    )

}
