import React, {useContext} from 'react';
import {EasyContext} from './Easy.jsx';

export default function Key({keyVal}) {

    const { board, setBoard, currTry, setCurrTry } = useContext(EasyContext);
    const selectLetter = () => {
        const newBoard = [...board];
        newBoard[currTry.noOfAttempt][currTry.letterPos] = keyVal;
        setBoard(newBoard);
        setCurrTry({...currTry, letterPos: currTry.letterPos + 1})
    }

    return (
        <div onClick = {selectLetter}>
            {keyVal}
        </div>
    )

}
