import React, {useEffect, useCallback} from 'react';
import Key from './Key.jsx';
import '../App.css';
import {EasyContext} from './Easy.jsx';

export default function Keyboard() {

    const { board, setBoard, currTry, setCurrTry, setSentense } = useContext(EasyContext);

    const keys1 = ["Q", "W", "E", "R", "T", "Y", "U", "I", "O", "P"];
    const keys2 = ["A", "S", "D", "F", "G", "H", "J", "K", "L"];
    const keys3 = ["Z", "X", "C", "V", "B", "N", "M"];

    const onSelectLetter = ((keyVal) => {
        if (currTry.letterPos >= letterLength) {
            setSentense("The Word is too long, honey :)")
            return;
        }
        setSentense("Everything looks good :)");
        const newBoard = [...board];
        newBoard[currTry.noOfAttempt][currTry.letterPos] = keyVal;
        setBoard(newBoard);
        setCurrTry({...currTry, letterPos: currTry.letterPos + 1})
    });

    const handleKeyboard = useCallback((event) => {
        if (event.key === "ENTER") {
            if (currTry.letterPos !== letterLength) {
                setSentense("The Word is too short, honey :)")
                return;
            }
            setSentense("Everything looks good :)");
            setCurrTry({noOfAttempt: currTry.noOfAttempt + 1, letterPos: 0});

        } else if (event.key === "DELETE") {
            if (currTry.letterPos === 0) {
                setSentense("You cannot delete empty item.")
                return;
            }
            setSentense("Everything looks good :)");
            const newBoard = [...board];
            newBoard[currTry.noOfAttempt][currTry.letterPos - 1] = "";
            setBoard(newBoard);
            setCurrTry({...currTry, letterPos: currTry.letterPos - 1})

        } else {
            keys1.forEach((key) => {
                if (event.key === key) {
                    onSelectLetter(key);
                }
            })
            keys2.forEach((key) => {
                if (event.key === key) {
                    onSelectLetter(key);
                }
            })
            keys3.forEach((key) => {
                if (event.key === key) {
                    onSelectLetter(key);
                }
            })
        }
    }) 

    useEffect(() => {
        document.addEventListener("keydown", handleKeyboard);

        return () => {
            document.removeEventListener("keydown", handleKeyboard);
        }
    }, [handleKeyboard])

    return (
        <div className = "keyboard" onKeyDown = {handleKeyboard}> 
            <div className = "line1">
                {keys1.map((key) => {
                    return (
                    <div className = "key"> 
                        <Key keyVal = {key} letterLength = {5}/>
                    </div>)
                })}
            </div>
            <div className = "line2">
                {keys2.map((key) => {
                    return (
                    <div className = "key"> 
                        <Key keyVal = {key} letterLength = {5}/>
                    </div>)
                })}
            </div>
            <div className = "line3">
                <div className = "key" id = "big"> 
                    <Key keyVal = "ENTER" letterLength = {5}/> 
                </div>
                {keys3.map((key) => {
                    return (
                    <div className = "key"> 
                        <Key keyVal = {key} letterLength = {5}/>
                    </div>)
                })}
                <div className = "key" id = "big"> 
                    <Key keyVal = "DELETE" letterLength = {5}/> 
                </div>
            </div>
        </div>
    )

}
